import React from 'react';
import { useState, useEffect, useRef } from 'react';
import "./LogIn.css";


function LogIn(){


    function handleLogInBtn(e) {
        e.preventDefault();
        console.log(e);
        const { userNameInp, passwordInp } = e.target.elements;
        const userName = userNameInp.value;
        const password = passwordInp.value;

        //fetch to login user 
        fetch('/api/users/login', {
            method: "POST",
            body: JSON.stringify({ userName, password }),
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res) => res.json())
            .then((data) => {
            console.log(data);
           /*      let token = Cookies.get('loginToken');
                if(token!=null){
                    const decoded = jwt.decode(token);
                    if(decoded.role==='Admin')
                    history.replace("Admin")
                    if(decoded.role==="QA manager")
                    history.replace("/status")
                    if(decoded.role==="TOP manager")
                    history.replace("/analysis")
                }
                else 
                alert("Username or password is incorrect") */

            });
    }
    return(
        <div className="logInContainer">
            <h4>LogIn</h4>
            <img src={process.env.PUBLIC_URL + "childTransportLogo.png"}></img><br></br>
            <label for="userName">User Name:</label>
            <input type="text" placeholder="User Name" id="userName" name="userNameInp"></input><br></br>
            <label for="password">Password:</label>
            <input type="text" placeholder="Password" id="password" name="passwordInp"></input><br></br>
            <p><a href="www.google.com">Forgot Password?</a></p>  {/*onclick={} */}
            <button id="LogInBtn" type="button" onClick={handleLogInBtn}>LogIn</button>
            <p>Don't have an account?<a href="www.google.com">Forgot Password?</a></p>

        </div>
            )
}
export default LogIn;
