import React from 'react';
import { useState, useEffect } from 'react';
import "./AccompanyingPersonChildCard.css";
import { Container, Navbar, Nav, Table, Button } from 'react-bootstrap';
import { useHistory } from "react-router-dom";

function AccompanyingPersonChildCard() {
    const history = useHistory();

    let AccompanyingPerson = localStorage.getItem("userID");
    const [childrenData, setChildrenData] = useState([]);
    useEffect(() => {

        fetch('/api/childrenCards/getAllChildrensCards', {
            method: 'POST',
            body: JSON.stringify({ AccompanyingPerson }),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(data => {
                if (data != null) {
                    setChildrenData(data.info);
                } else {
                    setChildrenData([]);
                    alert("you don't have any assigned children yet");
                }
            })
    }, []);




    function handleSettings() {
        history.replace("/SettingsPageAccompanyingPerson");
    }
    function handleHomePage() {
        history.replace("/AccompanyingPersonPage");
    }
    function handleLogOut() {

        const userID = AccompanyingPerson;

        //fetch to logout user 
        fetch('/api/users/logout', {
            method: "POST",
            body: JSON.stringify({ userID }),
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.success) {
                    localStorage.clear();
                    history.replace('/LogIn');
                }
                else {

                    alert("can't log out");
                }
            });
    }

    function handleFatherCall(props) {
        const fatherPhone = props;
        console.log(fatherPhone);
        //  window.open("https://api.whatsapp.com/send?phone=+972" + fatherPhone + "&text=&source=&data=");
    }

    function handleMotherCall(props) {
        const motherPhone = props;
        // console.log(motherPhone);
    }


    const renderChildren = (Child, index) => {
        return (
            
                <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{Child.childName}</td>
                    <td>{Child.childLastName}</td>
                    <td>{Child.childID}</td>
                    <td>{Child.gender}</td>
                    <td><Button variant="success" type="submit" id={Child.phoneNum}>Contact Mother</Button></td>
                    <td><Button variant="success" type="submit" id={Child.phoneNum2}>Contact Mother</Button></td>
                    <td> <Button variant="warning" block id="childCard" >Child Card</Button></td>
                </tr>
         
        )
    }

    var products = [{
        id: "1",
        name: "Item name 1",
        price: "100"
      },{
        id: "2",
        name: "Item name 2",
        price: "100"
      }];
      
      // It's a data format example.
      function priceFormatter(cell, row){
        return '<i class="glyphicon glyphicon-usd"></i> ' + cell;
      }

      function buttonFormatter(cell, row){
        return '<BootstrapButton type="submit"></BootstrapButton>';
      }
      
      React.render(
        <BootstrapTable data={products} striped={true} hover={true}>
            <TableHeaderColumn dataField="id" isKey={true} dataAlign="center" dataSort={true}>Product ID</TableHeaderColumn>
            <TableHeaderColumn dataField="name" dataSort={true}>Product Name</TableHeaderColumn>
            <TableHeaderColumn dataField="price" dataFormat={priceFormatter}>Product Price</TableHeaderColumn>
            <TableHeaderColumn dataField="button" dataFormat={buttonFormatter}>Buttons</TableHeaderColumn>
        </BootstrapTable>,
          
      );

    return (

        <div>
            <Container style={{ border: "solid #ffa500", borderColor: "#ffa500", background: `url('${process.env.PUBLIC_URL}/l.png')` }}>
                <Navbar className="mb-3" aria-label="Toolbar with Button groups">
                    <Nav className="mr-auto">
                        <Nav.Link href="#home" onClick={handleHomePage}>Home</Nav.Link>
                        <Nav.Link href="#Settings" onClick={handleSettings}>Settings</Nav.Link>
                        <Nav.Link href="#LogOut" onClick={handleLogOut}>Log Out</Nav.Link>
                    </Nav>
                </Navbar>
                <h3 style={{ fontWeight: "bold", color: "#ffa500", fontSize: "40px", fontFamily: "Times New Roman" }}>Children Cards</h3><br></br>
               {/*  <Table striped bordered hover responsive variant="dark" style={{ color: "white" }}>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>ID</th>
                            <th>Gender</th>
                            <th>Father's Phone Number</th>
                            <th>Mother's Phone Number</th>
                            <th>Child Card</th>
                        </tr>
                    </thead>
                    <tbody>
                        {childrenData.map(renderChildren)}
                    </tbody>
                </Table> */}
                
                <br></br>
            </Container>
        </div>
    )
}

export default AccompanyingPersonChildCard;
