import React from 'react';
import { useState, useEffect, useRef } from 'react';
import "./LogIn.css";
import {
    Link
} from "react-router-dom";
import { useHistory } from "react-router-dom";

function LogIn(){

    const history = useHistory();
    function handleLogInBtn(e) {
    
        e.preventDefault();
        console.log(e.target.elements);
        const { userNameInp, passwordInp } = e.target.elements;
        const userName = userNameInp.value;
        console.log(userName);
        const password = passwordInp.value;
        console.log(password);

        //fetch to login user 
        fetch('/api/users/login', {
            method: "POST",
            body: JSON.stringify({ userName, password }),
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res) => res.json())
            .then((data) => {
            console.log(data);
           /*      let token = Cookies.get('loginToken');
                if(token!=null){
                    const decoded = jwt.decode(token);
                    if(decoded.role==='Admin')
                    history.replace("Admin")
                    if(decoded.role==="QA manager")
                    history.replace("/status")
                    if(decoded.role==="TOP manager")
                    history.replace("/analysis")
                }
                else 
                alert("Username or password is incorrect") */

            });


       
    }

    function handleForgotPassword(){
        history.replace("/ForgotPassword")
    }
    return(
        <div className="logInContainer">
            <h4>LogIn</h4>
            <img src={process.env.PUBLIC_URL + "childTransportLogo.png"}></img><br></br>
            
            <form onSubmit={handleLogInBtn}>
            <label for="userName">User Name:</label>
            <input type="text" placeholder="User Name" id="userName" name="userNameInp" ></input><br></br>
            <label for="password">Password:</label>
            <input type="text" placeholder="Password" id="password" name="passwordInp" ></input><br></br>
            <button id="LogInBtn" type="submit" onKeyUp={handleLogInBtn}>LogIn</button>
            </form>
            <button type="button" id="ForgotPasswordBtn" onClick={this.handleForgotPassword}>Forgot Password?</button>
            <p>Don't have an account?<button >Sign up</button></p>

        </div>
            )
}
export default LogIn;
