import React from 'react';
import { useState, useEffect, useRef } from 'react';
import "./MyChildCard.css";
import { useHistory } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Container, FormGroup, Form } from 'react-bootstrap';

let childInfo = {
    childName: "",
    childLastName: "",
    childID: "",
    streetAdrees1: "",
    streetAdrees2: "",
    city: "",
    zipCode: "",
    gender: "",
};

let fatherInfo = {
    firstName: "",
    lastName: "",
    fatherID: "",
    phoneNum: "",
    homeAdrees: "",
    email: ""
}

let motherInfo = {
    firstName: "",
    lastName: "",
    motherID: "",
    phoneNum: "",
    homeAdrees: "",
    email: ""
}

let AccompanyingPersonInfo = {
    firstName: "",
    lastName: "",
    phoneNum: "",
    email: "",
    gender: ""
}

let schoolInfo = {
    schoolName: "",
    SchoolAdministrator: "",
    phoneNum: "",
    Adrees: "",
}


function MyChildCardInfo() {
    const [ChildData, setChildData] = useState([]);
    let history = useHistory();
    let myParent = localStorage.getItem("userID");
    console.log(myParent);
    useEffect(() => {
        fetch('/api/childrenCards/getMyChildInfo', {
            method: 'POST',
            body: JSON.stringify({ userID: localStorage.getItem("userID") }),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => res.json())
            .then(data => {
                console.log("returned from getMyChildInfo we are in MyChildCardInfo page")
                setChildData(data.info);
                console.log(data.info);


            })
    }, []);


    function ShowChild(props) {
        const { ChildData } = props;
        console.log("test");
        console.log(ChildData)
        return (
            <div className="MainBoxChildData">
               <p> {ChildData.childName}</p>
                        
            </div>
        )
    }
    return (

        ChildData.map((ChildData, index) => {
            return (<ShowChild key={index} ChildData={ChildData} />)
        })

    )
}
export default MyChildCardInfo;
